import { Router } from '@angular/router';
import { Component } from '@angular/core';
import { DataService } from '../data.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  showPassword = false;
  usuario: string = '';
  password: string = '';

  constructor(private dataService: DataService, private router: Router) { }

  togglePasswordVisibility() {
    this.showPassword = !this.showPassword;
  }

  onSubmit() {
    if (this.usuario && this.password) {
      this.dataService.autenticar(this.usuario, this.password).subscribe(
        (response) => {
          if (response.success) {
            alert('Inicio de sesión exitoso');
            // Redirige al usuario a otra página después de un inicio de sesión exitoso
            this.router.navigate(['/nosotros']);
          } else {
            alert(this.usuario + '+' + this.password);
            alert('Usuario o contraseña incorrectos.');
          }
        },
        (error) => {
          console.error('Error en la autenticación:', error);
          alert('Error en la autenticación. Consulta la consola para más detalles.');
        }
      );
    } else {
      alert('Rellene todos los campos por favor.');
    }
  }
}
<div class="container mt-5">
  <div class="row justify-content-center">
    <div class="col-md-6">
      <div class="card">
        <div class="card-header">
          <div class="image-container text-center">
            <img src="assets/logo_nivel_45.png" alt="Nivel 45" class="img-fluid logo-image">
          </div>
        </div>
        <div class="card-body">
          <h5 class="card-title">Iniciar Sesión</h5>
          <form (ngSubmit)="onSubmit()">
            <div class="mb-3">
              <label for="username" class="form-label">Usuario</label>
              <input type="text" class="form-control" id="username" name="username" placeholder="Usuario" [(ngModel)]="usuario" #usernameField (keydown.enter)="passwordField.focus()">
            </div>
            <div class="mb-3">
              <label for="password" class="form-label">Contraseña</label>
              <div class="input-group">
                <input [type]="showPassword ? 'text' : 'password'" class="form-control" id="password" name="password" placeholder="Contraseña" [(ngModel)]="password" #passwordField (keydown.enter)="onSubmit()">
                <span class="input-group-text password-toggle" (click)="togglePasswordVisibility()">
                  <i [class]="showPassword ? 'far fa-eye' : 'far fa-eye-slash'"></i>
                </span>
              </div>
            </div>
            <button type="submit" class="btn btn-primary">Iniciar Sesión</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
